VertexShader.Source:
uniform mat4 _World;
uniform mat4 _ViewProjection;

attribute vec4 a_pos;
attribute vec2 a_uv;
attribute vec4 a_color;

varying vec2 v_uv;
varying vec4 v_color;

void main() {
    gl_Position = a_pos * _World * _ViewProjection;
    v_uv = a_uv;
	v_color = a_color;
}

PixelShader.Source:
precision mediump float;

uniform sampler2D _MainTex;
uniform sampler2D _MainTexAlpha;

varying vec2 v_uv;
varying vec4 v_color;

void main() {
    gl_FragColor.rgb = texture2D(_MainTex, v_uv).rgb * v_color.rgb;
	gl_FragColor.a = texture2D(_MainTexAlpha, v_uv).r * v_color.a;
}
